import './App.css';
import React from 'react';
import {useState} from 'react';
function App() {
  const [time, setTime] = useState({ms:0, s:0, m:0, h:0});
  return (
    <div>
   <div className='watch'><span className='time' {...time.h}></span>:<span className='time' id='mins'>00</span>:<span className='time' id='secs'>00</span>:<span className='time' id='millsecs'>00</span></div>
   <br />
   <div className='btns'>
    <button className='btn'>Start</button>
    <button className='btn'>Pause</button>
    <button className='btn'>Lap</button>
    <button className='btn'>Restart</button>
   </div>
   <br />
   </div>
  );

}


export default App;
