import './App.css';
import React from 'react';
import {useState} from 'react';
function App(){
  const start = () => {
    run();
    setInterval(run, 10);
  };
  const [time, setTime] = useState({ms:0, s:0, m:0, h:0});
  const [interv, setInterv] = useState();
  const [status, setStatus] = useState(0);
  var upms = time.ms, ups = time.s, upm = time.m, uph = time.h;

  const run = () => {
    if(upm === 60){
      uph++;
      upm = 0;
    }
    if(ups === 60){
      upm++;
      ups = 0;
    }
    if(upms === 100){
      ups++;
      upms = 0;
    }
    upms++;
    return setTime({ms:upms, s:ups, m:upm, h:uph});

  }
  const reset = () =>{
    clearInterval(run);




  }
  return (
    <div>
   <div className='watch'><span className='time' >{(time.h >= 10) ? time.h : "0" + time.h}</span>:<span className='time'>{(time.m >= 10)? time.m : "0" + time.m}</span>:<span className='time'>{(time.s >= 10)? time.s : "0" + time.s}</span>:<span className='time'>{(time.ms >= 10)? time.ms : "0" + time.ms}</span></div>
   <br />
   <div className='btns'>
    <button className='btn' onClick={start}>Start</button>
    <button className='btn'>Pause</button>
    <button className='btn'>Lap</button>
    <button className='btn' onClick={reset}>Restart</button>
   </div>
   <br />
   </div>
  );

}


export default App;
