import './App.css';
import React from 'react';
import {useState} from 'react';
function App(){
  const [time] = useState({ms:0, s:0, m:0, h:0});
  return (
    <div>
   <div className='watch'><span className='time' >{(time.h <= 10)? time.h : "0" + time.h}</span>:<span className='time'>{(time.m <= 10)? time.m : "0" + time.m}</span>:<span className='time'>{(time.s <= 10)? time.s : "0" + time.s}</span>:<span className='time'>{(time.ms <= 10)? time.ms : "0" + time.ms}</span></div>
   <br />
   <div className='btns'>
    <button className='btn'>Start</button>
    <button className='btn'>Pause</button>
    <button className='btn'>Lap</button>
    <button className='btn'>Restart</button>
   </div>
   <br />
   </div>
  );

}


export default App;
